import React from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import {Link, useNavigate} from "react-router-dom"


export default function Login (props) {
    const [formData, setFormData] = React.useState(
        {username: "",
        email: "",
        password: "",}
    )
    const [getLoginRes, setGetLoginRes] = React.useState()
    const [isLoggedIn, setIsloggedIn] = React.useState(false)
    const [handleData, setHandleData] = React.useState()
                
let navigate = useNavigate();
React.useEffect(() => {
        if (isLoggedIn){
           return navigate("/");
        }
     },[isLoggedIn]);
React.useEffect(() => {
    displayLogin()
     },[getLoginRes]);


        function handleChange(event) {
            setFormData(prevFormData => {
                return {
                    ...prevFormData,
                    [event.target.name]: event.target.value
                }
            })
        }
            
        function handleSubmit(event) {
            event.preventDefault()
            console.log(formData)
        }
    
        const getLogin = async () => {
            console.log('post login acheived ');
            console.log(formData);
                let response = await Promise.resolve(fetch ('http://localhost:5000/login', {
                method: 'post', body: JSON.stringify(formData), //put your state from inputs/text area//),
                headers: { 'Content-Type': 'application/json' }
                }).then((res) => res.json()))
                console.log(response);
                let data = response
                console.log(data);
                setHandleData(data[0])
                console.log(handleData);
            }
                
        function displayLogin(){
            if(handleData !== {msg: 'Success! You are logged in.'}){
                setGetLoginRes(handleData)
            //   return data.msg //should be msg:'strng'
            }
            else{ 
                console.log('should go to profiel');
                setIsloggedIn(true)
            //   return data
            }
          }

return(
    <div>
        <Navbar/>
        <div>
        { getLoginRes ? <p>{getLoginRes}</p> : <p>{getLoginRes}</p>}
        </div>
        <div>
            <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        placeholder="Username"
                        onChange={handleChange}
                        name="username"
                        value={formData.username}
                    />
                    <input
                        type="text"
                        placeholder="Password"
                        onChange={handleChange}
                        name="password"
                        value={formData.password}
                    />
                    <input
                        type="email"
                        placeholder="Email"
                        onChange={handleChange}
                        name="email"
                        value={formData.email}
                    />
                    <button onClick={getLogin} >Login</button>
            </form>
                    <button><Link to={'/signup'}>Need To Sign Up?</Link></button>  
        </div>
        <Footer/>
    </div>
)}
