import React from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import {Link, Redirect} from "react-router-dom"

export default function Login (props) {
    const [formData, setFormData] = React.useState(
        {username: "",
        email: "",
        password: "",}
    )
    const [getLoginRes, setGetLoginRes] = React.useState('')
    const [isLoggedIn, setIsloggedIn] = React.useState(false)
            
        function handleChange(event) {
            setFormData(prevFormData => {
                return {
                    ...prevFormData,
                    [event.target.name]: event.target.value.toLowerCase()
                }
            })
        }
            
        function handleSubmit(event) {
            event.preventDefault()
            console.log(formData)
        }
    
        const getLogin = async () => {
            console.log('post login acheived ');
                let response = await Promise.resolve(fetch ('http://localhost:5000/login', {
                method: 'post', body: JSON.stringify(formData), //put your state from inputs/text area//),
                headers: { 'Content-Type': 'application/json' }
                }).then((res) => res.json()))
            if(data.msg !== 'Success! You are logged in.'){
                setGetLoginRes(data.msg)
              return data.msg //should be msg:'strng'
            }
            else{ 
              // set state to true that if true it will send location
              // to their profile page
              // or set state as data that will use react redirect when data matches something
            //   
            // if setGetLoginRes = Success! You are logged in. use react redirect to go to profile page
            setIsloggedIn(true)
              return data
            }
          }

return(
    <div>
        <Navbar/>
        <div>
        {isLoggedIn ? <Redirect to="/profile"/> : <p>{getLoginRes}</p>}
        </div>
        <div>
            <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        placeholder="Username"
                        onChange={handleChange}
                        name="username"
                        value={formData.username}
                    />
                    <input
                        type="text"
                        placeholder="Password"
                        onChange={handleChange}
                        name="password"
                        value={formData.password}
                    />
                    <input
                        type="email"
                        placeholder="Email"
                        onChange={handleChange}
                        name="email"
                        value={formData.email}
                    />
                    <button onClick={getLogin} >Login</button>
            </form>
                    <button><Link to={'/signup'}>Need To Sign Up?</Link></button>  
        </div>
        <Footer/>
    </div>
)}
